const color = 'blue'

const styles = css`
    border: 1px solid ${props => props.theme.colors.primary}
`

function SomeText (props) {
    const theme = useTheme()
    return (
        <div
            css={css`
                background-color: hotpink;
                &:hover {
                    color: ${color};
                }
            `}
            {...props}
        />
    )
}

