import styled from '@emotion/styled'
import { darken } from 'polished'

const StyledButton = styled.button`
    background-color: teal;
    border: none;
    border-radius: 4px;
    box-shadow: 3px 3px 0px ${darken(0.1, 'teal')};
    color: ${props => props.theme.colors.primary};
    font-size: 24px;
    font-weight: bold;
    padding: 32px;

    &:hover {
        color: white;
    }
`

export default () => <StyledButton>Hello</StyledButton>
