import styled from '@emotion/styled'

const ButtonStyles = styled.button`
    padding: 32px;
    background-color: hotpink;
    font-size: 24px;
    border-radius: 4px;
    color: black;
    font-weight: bold;
    &:hover {
        color: white;
    }
`

const Button = <button style={ButtonStyles}></button>

// Button = (<Button>This my button component.</Button>)

export default Button

